pragma solidity ^0.4.2;

/*

Contract Patent is used by main contract to create new patents (ideas).
It has array of created patents and patent constructor.
Contract owner can withdraw funds from contract.

*/
import "contracts/ownership/Owned.sol";
import "contracts/math/SafeMath.sol";
/* import "./ERC20.sol"; */


/**
*Contract Patent is used by main contract to create new patents (ideas).
*It has array of created patents and patent constructor.
*Contract owner can withdraw funds from contract.
*/
contract Patent is Owned {

    using SafeMath for uint;

    struct patentInfo {
        string patentHash;
        string name;
        string description;
        string author;
        uint date;
        uint8 patentType;
    }

    patentInfo[] public patents;

    function Patent(
        string _hash,
        string _name,
        string _description,
        string _author,
        uint8 _patentType,
        address creator
    ) public Owned(creator) {

        patentInfo memory newPatent = patentInfo({
            patentHash: _hash,
            name: _name,
            description: _description,
            author: _author,
            date: now,
            patentType: _patentType
            });

        patents.push(newPatent);
    }

    function withdraw(uint amount) onlyOwner public returns(bool) {
        require(amount <= address(this).balance);
        owner.transfer(amount);
        return true;

    }


    //      COMMENTED AS NOT NEEDED - YOU WILL NEED TO CHECK AND SHOW VAULT BALANCE
    //    function getBalance() public view returns(uint){
    //        return address(this).balance;
    //    }


    /**
    * Fallback function
    */
    function () public payable {
    }

}
